package homework;

public class Day01_4 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		short b=2;
		long c=3;
		byte d=4;
		float e=5.5f;
		double f=6.6;
		System.out.println("Now show a example of int"+a+".");
		System.out.println("Now show a example of short"+b+".");
		System.out.println("Now show a example of long"+c+".");
		System.out.println("Now show a example of byte"+d+".");
		System.out.println("Now show a example of float"+e+".");
		System.out.println("Now show a example of double"+f+".");
		System.out.println("Now show some examples of calculate.");
		System.out.println("+\t\ta+b="+(a+b));
		System.out.println("-\t\tb-c="+(b-c));
		System.out.println("*\t\tc*d="+(c*d));
		System.out.println("/\t\td/f="+(d/f));
	}

}
